package my_stuff

import (
	"go.temporal.io/sdk/workflow"
	"math/rand"
)

func MyWorkflow(context workflow.Context) {
	encodedRandom := workflow.SideEffect(context, func(ctx workflow.Context) interface{} {
		return rand.Intn(100)
	})
	var random int
	encodedRandom.Get(&random)
}

