func MyWorkflow(ctx workflow.Context) error {
  ...
  // setup proper retry policy for activities...
  f1 := workflow.ExecuteActivity(ctx, "MyBestEffortActivity", ...)
  err1 := f1.Get(ctx, nil)
  // here you can ignore err1 from MyBestEffortActivity and keep going 
  f2 := workflow.ExecuteActivity(ctx, "OtherActivity", ...)
  ...
}

